/**
 * Purchase Dto
 * Data Transfer Object for purchase message expected on back end
 * used for rest api message sent to back end
 *
 *
 */
export class PurchaseDto {
  constructor(customer, cart, cartItems) {
    this.customer = customer;
    this.cart = cart;
    this.cartItems = cartItems;
    this.nextIndex = 0;
  } // set customer


  setCustomer(customerIn) {
    this.customer = customerIn;
  } // get customer


  getCustomer() {
    return this.customer;
  } // set cart


  setCart(cartIn) {
    this.cart = cartIn;
  } // get cart


  getCart() {
    return this.cart;
  } // set cart item in cart items


  addCartItem(cartItemIn) {
    this.cartItems.push(cartItemIn);
    this.nextIndex += 1;
  } // get cart items


  getCartItems() {
    return this.cartItems;
  } // get most recent cart item index


  getCurrentCartItem() {
    let currentIndex = this.nextIndex - 1;
    return this.cartItems[currentIndex];
  }

}
